hudson.tasks.Shell.command:
#!/usr/bin/python import json import os json_file = open("config.json") config = json.loads(json_file.read()) json_file.close() config["eucalyptus"]["install-type"] = os.environ['method'] if "network" not in config["eucalyptus"]: config["eucalyptus"]["network"] = {} config["eucalyptus"]["network"]["public-interface"] = "br0" config["eucalyptus"]["network"]["private-interface"] = "br0"
config["eucalyptus"]["topology"] = { "clc-1": os.environ['hostname'], "walrus": os.environ['hostname'], "user-facing": [os.environ['hostname']], "clusters": {"default": { "cc-1": os.environ['hostname'], "sc-1": os.environ['hostname'], "nodes": os.environ['hostname'], "storage-backend": "overlay" }}}
json_file = open("config.json", 'w') json_file.write(json.dumps(config))
