hudson.tasks.Shell.command:
#!/bin/bash

cat > environment.json <<EOF
$environment
EOF
cat > bootstrap.json <<EOF
{
  "nodes": [
    {
      "groups": ["cloud::full","node::default"],
      "hosts": ["$hostname"]
    }
  ]
}
EOF

#!/usr/bin/python import json import os json_file = open("environment.json") environment = json.loads(json_file.read()) json_file.close() if "network" not in environment["default_attributes"]["eucalyptus"]: environment["default_attributes"]["eucalyptus"]["network"] = {} environment["default_attributes"]["eucalyptus"]["network"]["public-interface"] = "br0" environment["default_attributes"]["eucalyptus"]["network"]["private-interface"] = "br0"
environment["default_attributes"]["eucalyptus"]["topology"] = { "clc-1": os.environ['hostname'], "walrus": os.environ['hostname'], "user-facing": [os.environ['hostname']], "clusters": {"default": { "cc-1": os.environ['hostname'], "sc-1": os.environ['hostname'], "nodes": os.environ['hostname'], "storage-backend": "overlay" }}}
json_file = open("environment.json", 'w') json_file.write(json.dumps(environment))
env_name = open("environment-name", 'w') env_name.write(environment["name"])
